import React,{ Component } from 'react';
import './App.css';
import Person from './Person/Person';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput';
import Header from './Header/Header';
import Footer from './Footer/Footer';

class App extends Component {
	state = {
			persons: [
			{ name:'Max' , age:28 },
			{ name:'Manu' , age:29 },
			{ name:'Santosh' , age:18 }
			],
			nameDetails: [
				{ name: 'Suraj'},
				{ name: 'Kumar'}
			],
			otherState:'Some other value',
			showPerson:false
		};

	clickme = (event) =>{
		this.setState({
			persons: [
			{ name:'Maximum' , age:28 },
			{ name:'Manu', age:29 },
			{ name:'Santosh' , age:20 }
			]
		});
	}

	newNameHandler = (event) => {
		this.setState({
			persons:[
			{name:'Manu', age:26},
			{name:event.target.value,age:28},
			{name:'Santosh',age:29}
			]
		});
	}

	changeName = (event) =>{

		this.setState({
			nameDetails:[
				{ name: 'Sita'},
				{ name:'Pravin'}
			]
		});
	}

	editName = (event) => {
		this.setState({
			nameDetails:[
				{ name:event.target.value},
				{ name:event.target.value}
			]
		});
	}

	togglePersonHandler = () => {
		const tog = this.state.showPerson;
		this.setState({showPerson:!tog});
	}

	render(){

		let person = null;

		if(this.state.showPerson){

			person = (
				<div>
			     <UserOutput name={this.state.nameDetails[0].name} />
			     <UserOutput name={this.state.nameDetails[1].name} />
			    </div>
			);

		}

		const headstyle = {
			paddingTop:'20px',
			paddingBottom:'20px',
			color:'#917eb3'
		}

	return (
	    <div className="App">
	     <Header />
	     <h2 style={headstyle} >Welcome to React Js</h2>
	     <Footer />
	    </div>
	  );

	}
}

export default App;
