import React,{ Component } from 'react';
import './App.css';
import Person from './Person/Person';
import UserInput from './UserInput/UserInput';
import UserOutput from './UserOutput/UserOutput';

class App extends Component {
	state = {
			persons: [
			{ name:'Max' , age:28 },
			{ name:'Manu' , age:29 },
			{ name:'Santosh' , age:18 }
			],
			nameDetails: [
				{ name: 'Suraj'}
			],
			otherState:'Some other value'
		};

	clickme = (event) =>{
		this.setState({
			persons: [
			{ name:'Maximum' , age:28 },
			{ name:'Manu', age:29 },
			{ name:'Santosh' , age:20 }
			]
		});
	}

	newNameHandler = (event) => {
		this.setState({
			persons:[
			{name:'Manu', age:26},
			{name:event.target.value,age:28},
			{name:'Santosh',age:29}
			]
		});
	}

	// changeName = (event) =>{

	// 	this.setState({
	// 		nameDetails: [
	// 			{ name: 'Suraj'}
	// 		]
	// 	});
	// }


	render(){
	return (
	    <div className="App">
	     <h2>Welcome to React Js</h2>
	     <button onClick={this.clickme}>Switch Users</button>
	     <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
	     <Person name={this.state.persons[1].name} age={this.state.persons[1].age} changed={this.newNameHandler}> My hobbies:Racing </Person>
	     <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
	     <UserInput />
	     <button onClick={this.changeName}> Change Name </button>
	     <UserOutput name={this.state.nameDetails[0].name} />
	     <UserOutput name="Kumar" />
	    </div>
	  );

	}
}

export default App;
